import React, { useState } from 'react';

const Note = () => {
    const[note, setNote] = useState({
        title:"",
        content:""
    });
    const inputEvent = (e) =>{
        const name = e.target.name;
        const value = e.target.value;
        console.log(name);
        console.log(value);

    
        setNote((oldValue)=>{
            return{
                ...oldValue,
                [name]: value
            }
        })
    }
    return (
        <div className='w-5 d-flex justify-content-center form_container' >
            <form>
                <div className="row m-4">
                    <div className="col-12">
                        <div className="form-outline">
                            <input placeholder='Your Title here' name='title' value={note.title} onChange={inputEvent} type="text" id="form6Example1" className="form-control" />
                        </div>
                    </div>
                </div>
                <div>
                    <div className="col-12">
                    <div className="form-outline ">
                        <textarea placeholder='your notes here..' name='content' value={note.content} onChange={inputEvent} className="form-control w-5" id="form6Example7" rows="4"></textarea>
                    </div>
                    </div>
                   
                </div>
                <button type="submit" className="btn btn-primary">
                    +
                </button>
            </form>
        </div>
    )
}

export default Note
